changeSet.sql:
CREATE OR REPLACE FUNCTION compute_average_rating() RETURNS TRIGGER AS $$
    DECLARE
        ratings integer;
        r_count integer;
        average numeric(4,3);
    BEGIN
        SELECT SUM(rating) INTO ratings FROM
        (SELECT pr.rating FROM performer_rating AS pr WHERE performer_id = NEW.performer_id) AS prselect;
        SELECT COUNT(rating) INTO r_count FROM
        (SELECT pr.rating FROM performer_rating AS pr WHERE performer_id = NEW.performer_id) AS prselect;
        SELECT round((CAST(ratings AS NUMERIC)/r_count), 3) INTO average;
        UPDATE user_details SET average_rating = average WHERE id = NEW.performer_id;
        RETURN NEW;
    END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER performer_rating_t
AFTER INSERT OR UPDATE OR DELETE ON performer_rating FOR EACH ROW EXECUTE
PROCEDURE compute_average_rating();
